mapper.update:
UPDATE USER SET USER_PASSWORD =#{user_password}, USER_NAME=#{user_name}, USER_BIRTH=#{user_birth},
USER_GENDER=#{user_gender}, USER_EMAIL=#{user_email}, USER_PHONE=#{user_phone},
user_address1 = #{user_address1} , user_address2 = #{user_address2}
WHERE USER_ID=#{user_id}

mapper.select:
SELECT * FROM USER WHERE 1=1
 AND USER_PHONE = #{user_phone} 
 AND USER_EMAIL = #{user_email}
AND USER_TYPE = #{user_type}

mapper.update:
UPDATE USER
SET USER_PASSWORD =#{user_password}
WHERE
USER_ID=#{user_id}
